jQuery(document).ready(function($){console.log("load plugin example");document.addEventListener("wp_listen_for_consent_change",function(e){var changedConsentCategory=e.detail;for(var key in changedConsentCategory){if(changedConsentCategory.hasOwnProperty(key)){if(key==="marketing"&&changedConsentCategory[key]==="allow"){console.log("set marketing cookie on user actions");activateMarketing()}}}});$(document).on("wp_consent_type_defined",activateMyCookies);function activateMyCookies(consentData){if(wp_has_consent("marketing")){console.log("do marketing cookie stuff")}else{console.log("no marketing cookies please")}}if(!window.waitfor_consent_hook){console.log("we don't have to wait for the consent type, we can check the consent level right away!");if(wp_has_consent("marketing")){activateMarketing();console.log("set marketing stuff now!")}else{console.log("No marketing stuff please!")}}function activateMarketing(){console.log("fire marketing");$("#example-plugin-content .functional-content").hide();$("#example-plugin-content .marketing-content").show()}});